import { resolve } from "path";
import fsExtraPkg from "fs-extra";
import { integrationConfig, netlifyToml } from "../../constants/handler.js";
const { writeFile } = fsExtraPkg;
export const init = async (opts) => {
    const pwd = process.cwd();
    const { slug } = opts;
    const toml = netlifyToml();
    const config = integrationConfig(slug);
    await writeFile(resolve(pwd, "netlify.toml"), toml);
    await writeFile(resolve(pwd, "integration.yaml"), config);
};
