import { Worker } from "worker_threads";
export class IntegrationWorkerManager {
    worker;
    integrationPath;
    constructor(integrationPath) {
        this.integrationPath = integrationPath;
    }
    loadWorker() {
        const worker = new Worker(new URL("./worker.js", import.meta.url), {
            workerData: {
                integrationPath: this.integrationPath,
            },
        });
        this.worker = worker;
        return worker;
    }
    async reload() {
        this.stop();
        this.loadWorker();
        await this.loadModule();
        return this;
    }
    stop() {
        if (this.worker) {
            this.worker.terminate();
            this.worker = undefined;
        }
    }
    async getMessageResponse(type, payload) {
        return new Promise((resolve) => {
            if (!this.worker) {
                this.worker = this.loadWorker();
            }
            const id = Math.random().toString(36).substring(7);
            const messageHandler = (message) => {
                if (message.id === id) {
                    this.worker?.off("message", messageHandler);
                    resolve(message.result);
                }
            };
            this.worker.on("message", messageHandler);
            this.worker.postMessage({
                id,
                type,
                payload,
            });
        });
    }
    async loadModule() {
        if (!(await this.getMessageResponse("loadModule"))) {
            throw new Error("Failed to load integration worker.");
        }
        return this;
    }
    implementedConnectorAPINames() {
        return this.getMessageResponse("implementedConnectorAPINames");
    }
    writeConnectorOptionsSchema() {
        return this.getMessageResponse("writeConnectorOptionsSchema");
    }
}
