export const handler = (path, config) => `import { integration } from '${path}';
integration._slug = '${config.slug}';
export const handler = integration.baseHandler;`;
export const netlifyToml = () => `[build]
command="netlify-integration build -a"
publish=".ntli/site/static"

[functions]
directory=".ntli/site/netlify/functions"
node_bundler = "esbuild"

[[headers]]
for="/ui/*"
[headers.values]
Access-Control-Allow-Origin="*"
Access-Control-Allow-Headers="*"
Access-Control-Allow-Methods="*"

[dev]
autoLaunch = false
functions = ".ntli/site/netlify/functions"
publish = ".ntli/site/static"
`;
export const integrationConfig = (slug = "new-integration") => `# yaml-language-server: $schema=https://sdk.netlify.com/config-schema.json
config:
  slug: ${slug}`;
